import React, {useState} from 'react'
import app from './Confi/firebase.js';
import {getAuth, createUserWithEmailAndPassword} from 'firebase/auth'
import {getFirestore, doc, setDoc} from "firebase/firestore"

const auth = getAuth(app);
const firestore = getFirestore(firestore);

function Login() {

  const [Registrando, setRegistrando] = useState(false);

  async function  registrarusuario(email,password){
   const infoUsuario = await createUserWithEmailAndPassword(auth,
    email,password).then((usuarioFirebase) => {
    return usuarioFirebase;
   });

    console.log(infoUsuario.user.uid);
    const docRef = doc(firestore, `usuario/${infoUsuario.user.uid}`);
  }
  
  function submitHandler(e){
   e.preventDefault();

   const email = e.target.elements.email.value;
   const password =  e.target.elements.password.value;

    console.log('submit', email,password);

    if(Registrando){
     registrarusuario(email,password);
    }else {
      
    }
  }
  return(
    <div>
     <h1>{Registrando ? "Registrate " : "Iniciar sesion"} </h1>

     <form onSubmit={submitHandler}> 
     <label> Correo: 
     <input type="email" id="email" />
     </label>
     <label> Password: 
     <input type="password" id="password" />
     </label>
     <input type="submit"  value={Registrando ? "Registrar" : "Iniciar secion"}/>
     </form>
     <button onClick={() => setRegistrando(!Registrando)}>
     {Registrando ? "Tengo una cuenta" : "Crear cuenta"}
     </button>
    </div>
    )

  
}

export default Login;
