import React, {useState} from "react";
import  db from "./Confi/firebase";
import {collection,getDocs,addDoc} from "firebase/firestore";
import { Navigate } from "react-router-dom";
import app from "./Confi/firebase";

const SignUp = () => {

  const [NewTitulo, setNewTitulo] = useState();

  const [currentUser, setCurrentUser] = useState(null);    
  const handleSubmit = (e) => {
    e.preventDefault();  
    const postCollection = collection(db, "usuario");  
    const { email, password,nombre, apellido, usuario } = e.target.elements;
    try {
      app.auth().createUserWithEmailAndPassword(email.value, password.value);
     addDoc(postCollection, { Email: email, Password: password, Nombre: nombre, Apellido: apellido, Usuario:usuario });
      
      setCurrentUser(true);
    } catch (error) {
      console.log(error);
    }
  };
  if (currentUser) {
      return <Navigate to="/dashboard" />;
  }
  return (
    <>
      <h1>Sign Up</h1>
      <form onSubmit={handleSubmit}>
        <label >Email</label>
        <input type="email" name="email" placeholder="Email"  onChange={(event) => {
          setEmail(event.target.value);}} />
        <label >Password</label>
        <input type="password" name="password" placeholder="Password" />

        <label >Nombre</label>
        <input type="text" name="nombre" placeholder="Nombre" />
        <label >Apellido</label>
        <input type="text" name="apellido" placeholder="Apellido" />

        <label >Usuario</label>
        <input type="text" name="usuario" placeholder="Usuario" />

        <button type="submit">Submit</button>
      </form>
    </>
  );
};

export default SignUp;
