import React, {useState} from 'react'
import VentanaModal from "./VentanaModal"
import AgregarPost from './AgregarPost.js';


const Navbar = () => {

const VentanaModal = () => {
  const [show, setShow] = useState(false);
   
  const handleModalClose = (e) => {
    const currentClass = e.target.className;
    
    if (currentClass === 'modal-card') {
      return;
    }
    
    setShow(false);
  };
  
  const handleModalOpen = () => {
    setShow(true);
  };
  
  return (
    <div className="App">
      <div
        hidden={!show}  
      >
        <div
          className="modal-background"
          onClick={handleModalClose}
        >
          <div className="modal-card">
            <AgregarPost />
          </div>
        </div>
      </div>
      <button
        className="button"
        onClick={handleModalOpen}  
      >
        Open Modal
      </button>
    </div>
  );
}

return(

<nav className="navbar navbar-dark bg-dark "> 
<div className="container ">

<a className="navbar-brand" href="/"><h3>Muro interactivo -BTS-</h3></a>
  <div className="d-grid gap-2 d-md-flex justify-content-md-end">
<button className="btn btn-outline-light me-md-2" type="button" >Iniciar secion</button>
<button className="btn btn-outline-light" type="button" onClick={handleModalOpen} ><i className="material-icons">add</i>

</button>
</div>
 </div>
</nav>


)
}

export default Navbar;
