import React from 'react'
import Navbar from './Navbar.js'
import Carrusel from './Carrusel.js'
import ListarPost from './ListarPost';


function Home() {

  const [user, setUser] = useState(null);

  function setUserWithFirebase(UsuarioFirebase){
    const userData = {
        uid: UsuarioFirebase.uid,
        email: UsuarioFirebase.email,
        password: UsuarioFirebase.password
      };
      setUser(userData);
      console.log('UserData Final', userData);
  }

  onAuthStateChanged(auth,(UsuarioFirebase) => {
    if(UsuarioFirebase){
      if(!user){
        setUserWithFirebase(UsuarioFirebase);
      }
    }else{
      setUser(null);
    }
  });
  
return(
  <div>
  <Navbar />
  <div>
   <h4>Formulario de resgitro</h4>
    <Registrar />
    </div>
    {user ?  <Home2 /> :  <Home />}
  <div className="container"> 
   <Carrusel />
  <br/>
   <div className="alert alert-dark" role="alert">
   <h4>Para agregar un nuevo post debe iniciar sesion o registrarse</h4></div>  
  <ListarPost />
  <br/>
   </div>
  </div>
  )


}

export default Home;
