import { useState, useEffect } from "react";
import  db from "./Confi/firebase";
import {collection,getDocs,addDoc} from "firebase/firestore";


function Registrar() {

  const [NombreUsuario, setNombreUsuario] = useState();
  const [Nombre, setNombre] = useState();
  const [Apellido, setApellido] = useState();
  const [Correo, setCorreo] = useState();
  const [Password, setPassword] = useState();



  const [post, setUsuario] = useState([]);
  const postCollection = collection(db, "usuario");

  const createUsuario = async (e) => {
  	e.preventDefault();
    await addDoc(postCollection, { usuario: NombreUsuario, nombre: Nombre, apellido: Apellido, correo: Correo, password: Password });
  };



    useEffect(() => {
    const getUsuario = async () => {
      const data = await getDocs(postCollection);
      setUsuario(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };

    getUsuario();
  }, []);

	return(
     <form > 
     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">Usuario</i>
     </div>
     <input type="text" className="form-control" placeholder="usuario" name="usuario" onChange={(event) => {
          setNombreUsuario(event.target.value);
        }}/>
     </div>
      <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">Nombre</i>
     </div>
     <input type="text" className="form-control" placeholder="nombre" name="nombre" onChange={(event) => {
          setNombre(event.target.value);
        }}/>
     </div>
     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">Apellido</i>
     </div>
      <input type="text" className="form-control" placeholder="apellido" name="apellido" onChange={(event) => {
          setApellido(event.target.value);
        }}/>
     </div>
     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">Apellido</i>
     </div>
     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">Correo</i>
     </div>
     <input type="text" className="form-control" placeholder="Correo" name="correo" onChange={(event) => {
          setCorreo(event.target.value);
        }} />
     </div>
      <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">Clave</i>
     </div>
     <input type="text" className="form-control" placeholder="Clave" name="password" onChange={(event) => {
          setPassword(event.target.value);
        }} />
     </div>
     </div>
     <button className="btn btn-primary btn-block" onClick={createPost}> Guardar Post </button>
    
     </form>
          
           
		)
     
};

export default AgregarPost;
