import { useState, useEffect } from "react";
import  db from "./Confi/firebase";
import {collection,getDocs,addDoc} from "firebase/firestore";


function Registrar() {

  const [NombreUsuario, setNombreUsuario] = useState();
  const [Nombre, setNombre] = useState();
  const [Apellido, setApellido] = useState();
  const [Correo, setCorreo] = useState();
  const [Password, setPassword] = useState();



  const [post, setUsuario] = useState([]);
  const postCollection = collection(db, "usuario");

  const createUsuario = async (e) => {
  	e.preventDefault();
    await addDoc(postCollection, { usuario: Usuario, nombre: Nombre, apellido: Apellido, correo: Correo, password: Password });
  };



    useEffect(() => {
    const getUsuario = async () => {
      const data = await getDocs(postCollection);
      setPost(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };

    getUsuario();
  }, []);

	return(
     <form > 
     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">Usuario</i>
     </div>
     <input type="text" className="form-control" placeholder="usuario" name="usuario" onChange={(event) => {
          setUsuario(event.target.value);
        }}/>
     </div>
      <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">title</i>
     </div>
     <input type="text" className="form-control" placeholder="nombre" name="nombre" onChange={(event) => {
          setNombre(event.target.value);
        }}/>
     </div>
     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">create</i>
     </div>
     <textarea name="contenido" rows="4" className="form-control" placeholder="Escriba el contenido e su post" onChange={(event) => {
          setNewContenido(event.target.value);
        }}></textarea>
     </div> 

     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">person</i>
     </div>
     <input type="text" className="form-control" placeholder="Autor" name="autor" onChange={(event) => {
          setNewAutor(event.target.value);
        }} />
     </div>
     <button className="btn btn-primary btn-block" onClick={createPost}> Guardar Post </button>
    
     </form>
          
           
		)
     
};

export default AgregarPost;
