import React, {useState} from 'react'

const FormPost = (props)=>{

 const initialStateValue = {
   	 titulo:'',
   	 contenido:'',
   	 imagen:'',
   	 autor:'',
   };

 const [values, setValues] = useState(initialStateValue);

	const handleInputChange = (e) => {
		const {name, value} = e.target;
		setValues({...values, [name] : value})

	};


	const handleSubmit = (e) => {
		e.preventDefault();
       	props.addOrEdit(addOrEdit);
	};

	return(
     <form onSubmit={handleSubmit}> 
     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">title</i>
     </div>
     <input type="text" className="form-control" placeholder="Titulo" name="titulo" onChange={handleInputChange}/>
     </div>

     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">create</i>
     </div>
     <textarea name="contenido" rows="4" className="form-control" placeholder="Escriba el contenido de su post" onChange={handleInputChange}></textarea>
     </div>

     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">image</i>
     </div>
     <input type="file" name="imagen" className="form-control" onChange={handleInputChange}/>
     </div>

     <div className="form-group input-group">
     <div className="input-group-text bg-light">
        <i className="material-icons">person</i>
     </div>
     <input type="text" className="form-control" placeholder="Autor" name="autor" onChange={handleInputChange}/>
     </div>
     <button className="btn btn-primary btn-block"> Guardar Post </button>
     </form>
		)
     
};

export default FormPost;
