import React, {useState} from 'react';
import FormPost from './FormPost.js';
import db from './Confi/firebase';
import {doc, collection, query} from "firebase/firestore";

const AgregarPost = ()=>{

	const pp = doc(db, 'post');


 const addOrEdit= (PostObject) =>{
 	const p = query(collection(db, "buddies"))
 	p.doc().set(PostObject);
 	console.log('nuevo post agregado');
 };
 return (
     <div>
     <FormPost addOrEdit={addOrEdit}/>

     </div>
 	)
}

export default AgregarPost;
