import React, {useState, useEffect} from 'react'
import { db } from "./Confi/firebase";
import fotocard from './img/g.jpg'
import {collection,getDocs,doc} from "firebase/firestore";

const ListarPost = ()=>{

	const [post, setPost] = useState([]);
	const postCollection = collection(db, "post");


	useEffect(() => {
		const getPost = async () => {
			const data = await getDocs(postCollection);
			setPost(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
		};

		getPost();
	}, []);

	return(
		<div className="row"> {post.map((post) => {
			return (
            <div className="col-sm-4" key={post.id}> 
			<div className="card">
			<img className="" src={fotocard} />{post.Imagen}
			<div className="card-body">
			<h3 className="card-title">{post.Titulo}</h3>
			<p className="card-text">{post.Contenido}</p>
			<h5 className="card-text" >Autor:</h5>{post.Autor}
			</div>
			</div>
			</div>
				);
		})}
		 </div>

		);


	}

	export default ListarPost;
