import React from 'react';
import style from 'styled-components'
import AgregarPost from './AgregarPost.js'


const Modal = () => {
	return(
     <>
     <OverLay>
      <ContenedorModal>
      <TituloModal>
       <h2>Agrega un nuevo post</h2>
      </TituloModal>
      <AgregarPost />
      <BotonCerrar> 
      <i className="material-icons">close</i>
      </BotonCerrar>
      </ContenedorModal>
     </OverLay>
     </>
		);
}


export default Modal;


const OverLay = style.div`
     width: 100vw;
     height: 100vh;
     position: fixed;
     top: 0;
     left:0;
     background: rgba(0,0,0,.5);
     padding:40px;
     display: flex;
     align-items: center;
     justify-content: center;
`;

const ContenedorModal = style.div`
    width: 500px;
    min-height: 100px;
    background: #fff;
    position: relative;
    border-radius: 5px:
    box-shadow: rgba(100,100,111, 0.2) 0px 7px 29px 0px;
    padding: 20px;

`;

const TituloModal = style.div`
     display: flex;
     align-items: center;
     justify-content: space-between;
     margin-bottom: 20px;
     paddig-bottom: 20px;
     border-bottom: 1px solid #E8E8E8;

     h2{
     	font-weight: 500;
     	font-size: 16px;
     	color: #1766DC:
     }
`;

const BotonCerrar = style.button`
     position: absolute;
      top: 20px;
      right:20px;

      width:30px;
      height:30px;
      border: none;
      background: none;
      cursor: pointer;


`;
