import React, {useState} from 'react'
import FormPost from './FormPost.js'
import db from './Confi/firebase'
import {collection } from "firebase/firestore";


const AgregarPost = ()=>{

 const addOrEdit= async (PostObject) =>{
 	await db(collection(db, "postMuro").doc().set(PostObject));
 	console.log('nuevo post agregado');
 };
 return (
     <div>
     <FormPost addOrEdit={addOrEdit}/>

     </div>
 	)
}

export default AgregarPost;
