import React, {Component} from 'react';
import app from './Confi/firebase.js'

export default class CreacionUsuario extends Component {
    constructor(props){
        super(props)
        this.state = {
            email:'',
            password:'',
            nombre: '',
            apellido: '',
            usuario: '',
        }
        this.handleInput=this.handleInput.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }
    handleSubmit(e){
        e.preventDefault();
        console.log(this.state)
        app.auth().createUserWithEmailAndPassword(this.state.email, this.state.contraseña)
        .then(()=>{
            var user=app.auth().currentUser
            console.log(user)
            app.database.ref('usuario').child(user.uid).update({
                email: this.state.email,
                nombre: this.state.nombre,
                edad: this.state.edad,
                peso: this.state.peso,
                estatura: this.state.estatura
            })
        })
    }
    handleInput(e){
        this.setState({
          [e.target.name]: e.target.value
        })
    }
    render(){
        return (
            <form>
              Email: <br />
              <input type="email" name="email" onChange={this.handleInput}/><br />
              Contraseña: <br />
              <input type="password" name="contraseña" onChange={this.handleInput}/><br />
              Nombre: <br />
              <input type="text" name="nombre" onChange={this.handleInput}/><br />
              Apellido: <br />
              <input type="text" name="apellido" onChange={this.handleInput}/><br />
              Usuario: <br />
              <input type="text" name="usuario" onChange={this.handleInput}/><br />

              <input type="submit" value="Crear" onClick={this.handleSubmit} />
            </form>
        )
    }
}
