	 state = {
    data: [],
    form:{
      titulo: '',
      contenido: ''
    },
    id: 0
  };

  peticionGet = () => {
    firebase.child("Post").on("value", (canal) => {
      if (canal.val() !== null) {
        this.setState({ ...this.state.data, data: Post.val() });
      } else {
        this.setState({ data: [] });
      }
    });
  };

  peticionPost=()=>{
    firebase.child("Post").push(this.state.form,
      error=>{
        if(error)console.log(error)
      });
      this.setState({});
  }
  
   handleChange=e=>{
    this.setState({form:{
