import React, {useState} from 'react';
import Home from "./Home"
import Home2 from "./Home2"
import './App.css'
import Registrar from './Registrar'
import firebaseConfig from './Confi/firebase2.js'
import {getAuth, onAuthStateChanged} from "firebase/auth";

const auth = getAuth(firebaseConfig);

function App() {

  const [user, setUser] = useState(null);

  function setUserWithFirebase(UsuarioFirebase){
    const userData = {
        uid: UsuarioFirebase.uid,
        email: UsuarioFirebase.email,
        password: UsuarioFirebase.password
      };
      setUser(userData);
      console.log('UserData Final', userData);
  }

  onAuthStateChanged(auth,(UsuarioFirebase) => {
    if(UsuarioFirebase){
      if(!user){
        setUserWithFirebase(UsuarioFirebase);
      }
    }else{
      setUser(null);
    }
  });

  return (
    <>
    <div>
  <Navbar />
  <div className="container"> 
   <Carrusel />
  <br/>
   <div className="alert alert-dark" role="alert">
   <h4>Para agregar un nuevo post debe iniciar sesion o registrarse</h4></div>  
  <ListarPost />
  <br/>
   </div>
  </div>
    <div className="container">
    <Registrar />
    </div>
    {user ?  <Home2 /> :  <App />}

    </>

  );
}

export default App;
